mapper.select:
select d.*, c.name as categoryName from dish d left join category c on d.category_id = c.id


        and d.name like concat('%', #{name}, '%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from dish

    and id = #{id}
    and name = #{name}
    and category_id = #{categoryId}
    and price = #{price}
    and image = #{image}
    and description = #{description}
    and status = #{status}
    and create_time = #{createTime}
    and update_time = #{updateTime}
    and create_user = #{createUser}
    and update_user = #{updateUser}

order by create_time desc;
